import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import { Col, Form, Row } from 'react-bootstrap';
import Volunteer from './Volunteer';

const ShowVolunteerList = () => {

    const [register, setRegister] = useState([])
    //const {_id} = register
    useEffect(() => {
        fetch('http://localhost:4000/volunteer')
        .then(res => res.json())
        .then(data => setRegister(data))

    }, [])

    function Delete (id) {
        console.log(id)
        fetch(`http://localhost:4000/delete/${id}`, {
            method: 'DELETE'
        })
        .then(res => res.json())
        .then(data => {
            console.log('deleted successfully')
        })

    }

    return (
        <div>
            <h3>you have {register.length} items</h3>
            <Row>
                <Col>
                    <Form.Label type="text">Name</Form.Label>

                </Col>
                <Col>
                    <Form.Label type="text">Email</Form.Label>

                </Col>
                <Col>
                    <Form.Label type="text">RegisterDate</Form.Label>

                </Col>
                <Col>
                    <Form.Label type="text">Volunteer List</Form.Label>

                </Col>
            </Row>
            {
                register.map(reg => <Volunteer reg={reg}></Volunteer>)
            }
            {/* {
                register.map(reg => <li>Name: {reg.displayName} Email: {reg.email} RegiterDate: {reg.enterDate} 
                <button onClick={()=> Delete(`${reg._id}`)}>Delete</button></li>)
            } */}

        </div>
    );
};

export default ShowVolunteerList;
