import React, { useContext } from 'react';
import { Button, Card, Col, Container, Form, Row } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
import { UserContext } from '../../App';
import './Register.css'

const Register = () => {

    const {category} = useParams()
    const [loggedInUser] = useContext(UserContext)



    return (
        <Container>
        <Row style={{ marginTop: '80px',color:'black' }}>
            <Col className="destination" >
                <Card.Body>
                    <Card.Title style={{ fontSize: '60px', color:'black' }}>{category}</Card.Title>
                    <Card.Text style={{color:'black'}}>
                        Name: {loggedInUser.displayName}
                        <br/>
                        Email: {loggedInUser.email}
                    </Card.Text>
                </Card.Body>

            </Col>
            <Col >
                <Form className="form" >

                    <Form.Group controlId="formBasicPassword">
                        <Form.Label type="text">Name</Form.Label>
                        <Form.Control type="text" value={loggedInUser.displayName} />
                        {/* <input type="text" placeholder={loggedInUser.displayName}></input> */}
                    </Form.Group>

                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>Description</Form.Label>
                        <Form.Control type="text" />
                    </Form.Group>

                    <Row style={{ padding: '20px' }}>
                         <Form.Group>
                                <Form.Label>Date</Form.Label>
                                <Form.Control type="date" />

                            </Form.Group>
                    </Row>

                    <Button  style={{ marginLeft: '5px' }} variant="warning" size="md" block>
                        Register
                    </Button>
                </Form>

            </Col>
        </Row>
    </Container>
    );
};

export default Register;
